mapper.select:
select * from food.tb_admin


        and id=#{id}


        and pwd=#{password}

mapper.update:
update tb_admin
set login_times=login_times+1,
    last_login_time=#{date}
where id=#{id};

update tb_admin
set username = #{username},gender=#{gender},tel=#{tel},email=#{email}
where id=#{id};

update tb_admin
set pwd = #{password}
where id=#{id};

update tb_admin
set tb_admin.state= #{mg_state}
where id=#{id};
